/*Table structure for table `yuqi_adminmenus` */

DROP TABLE IF EXISTS `yuqi_adminmenus`;

CREATE TABLE `yuqi_adminmenus` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `parent_id` int(11) unsigned NOT NULL DEFAULT '0',
  `uri` varchar(255) CHARACTER SET latin1 NOT NULL DEFAULT '',
  `view_order` int(10) NOT NULL DEFAULT '0',
  `target` int(11) NOT NULL DEFAULT '0',
  `app` enum('admin','index','space') NOT NULL DEFAULT 'admin',
  PRIMARY KEY (`id`),
  KEY `FK_yuqi_adminmenus_1` (`parent_id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

/*Data for the table `yuqi_adminmenus` */

insert  into `yuqi_adminmenus`(`id`,`name`,`parent_id`,`uri`,`view_order`,`target`,`app`) values (1,'用户管理',0,'',0,0,'admin'),(2,'文章管理',0,'',0,0,'admin'),(3,'修改密码',1,'user/editPassword',1111,0,'admin'),(4,'退出',1,'user/logout',2222,1,'index'),(5,'添加分类',2,'category/add',0,0,'admin'),(6,'分类管理',2,'category/manage',0,0,'admin'),(7,'添加文章',2,'article/add',0,0,'admin'),(8,'文章管理',2,'article/manage',0,0,'admin');

/*Table structure for table `yuqi_article_category` */

DROP TABLE IF EXISTS `yuqi_article_category`;

