/*Table structure for table `yuqi_articles` */

DROP TABLE IF EXISTS `yuqi_articles`;

CREATE TABLE `yuqi_articles` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(200) NOT NULL,
  `summary` text NOT NULL,
  `content` text NOT NULL,
  `user_id` int(10) unsigned NOT NULL,
  `create_time` bigint(20) unsigned NOT NULL,
  `status` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `read_times` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读次数',
  `update_time` bigint(20) unsigned NOT NULL DEFAULT '0',
  `ref` tinyint(3) unsigned NOT NULL COMMENT '0 原创 1转贴 2翻译',
  `refurl` varchar(511) DEFAULT NULL COMMENT 'ref=0时为null',
  PRIMARY KEY (`id`),
  KEY `FK_yuqi_articles_1` (`user_id`),
  CONSTRAINT `FK_yuqi_articles_1` FOREIGN KEY (`user_id`) REFERENCES `yuqi_users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=241 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

